/**
 * The {@link Application} module facilitates creation of secure web-based user interface of {@link PanelPage}'s, allowing decision makers to interact in real-time with ELARA and third party systems.
 *
 * @module Application
 *
 * @example
 * ```typescript
 * import { Schema, EnvironmentVariable, ApplicationPlugin, Environment } from "@elaraai/elara"
 *
 * // for example using an ApplicationPlugin with the Application module, create a simple secure application to view logs, weather and holidays.
 * export default Schema(
 *      ApplicationPlugin({
 *          name: 'Weather & Holidays',
 *          contents: mergeSchema(
 *              WeatherPlugin(),
 *              HolidaysPlugin(),
 *          ),
 *          users: [
 *              User({
 *                  email: "support@elara.ai",
 *                  name: "Support",
 *                  password: Environment('ADMIN_PASSWORD'),
 *                  is_disabled: false,
 *                  is_superuser: true,
 *                  is_admin: true
 *              })
 *          ],
 *          environments: [
 *              EnvironmentVariable({ name: "ADMIN_PASSWORD" }),
 *          ]
 *      })
 * )
 * ```
 */
export * from './Annotation';
export * from './Application';
export * from './Axis';
export * from './Color';
export * from './Interaction';
export * from './Page';
export * from './Series';
export * from './Visual';
